    public static ArrayList<Tourism> getListData() {
        Tourism tourism;
        ArrayList<Tourism> list = new ArrayList<>();
        for (int i = 0; i < data.length; i++) {
            tourism = new Tourism();

            tourism.setImg(data[i][0]);
            tourism.setTitle(data[i][1]);
            tourism.setLocation(data[i][2]);
            tourism.setDesc(data[i][3]);


            list.add(tourism);

        }
        return list;
    }
